import { useCallback } from 'react';
import { verifySupplier } from '@/services/playwright';

export const useSupplierVerification = () => {
  const verify = useCallback(async (product: Product) => {
    try {
      const result = await verifySupplier({
        productName: product.productName,
        supplierName: product.supplier,
        specifications: product.specifications
      });

      return {
        ...product,
        verifiedSupplier: result.verified,
        priceRange: result.priceRange,
        productImage: result.productImage,
        verificationStatus: result.verified ? 'verified' : 'failed'
      };
    } catch (error) {
      return {
        ...product,
        verificationStatus: 'failed'
      };
    }
  }, []);

  return { verify };
};
