'use client';
import { useSupplierVerification } from '@/hooks/useSupplierVerification';

interface SupplierCellProps {
  product: VerifiedProduct;
  onUpdate: (updated: VerifiedProduct) => void;
}

export const SupplierCell = ({ product, onUpdate }: SupplierCellProps) => {
  const { verify } = useSupplierVerification();

  const handleVerify = async () => {
    const updated = { ...product, verificationStatus: 'pending' };
    onUpdate(updated);
    const result = await verify(product);
    onUpdate(result);
  };

  return (
    <div className="flex items-center gap-2">
      <input
        value={product.supplier}
        onChange={(e) => onUpdate({ ...product, supplier: e.target.value })}
        className="flex-1 border rounded px-2 py-1"
      />
      <VerifyButton 
        status={product.verificationStatus}
        onClick={handleVerify}
        disabled={!product.supplier}
      />
    </div>
  );
};
