import { chromium } from 'playwright-core';

export const verifySupplier = async (params: {
  productName: string;
  supplierName: string;
  specifications?: string;
}) => {
  const browser = await chromium.launch();
  const page = await browser.newPage();

  try {
    await page.goto(`https://www.made-in-china.com/search?q=${encodeURIComponent(params.productName)}`);

    // Extract structured data
    const productData = await page.evaluate(() => {
      const scripts = Array.from(document.querySelectorAll('script[type="application/ld+json"]'));
      return scripts.map(script => {
        try { return JSON.parse(script.textContent); } catch { return null; }
      }).filter(Boolean);
    });

    const match = productData.find((p: any) => 
      p?.item?.name?.includes(params.productName) && 
      p?.item?.offers?.seller?.name?.includes(params.supplierName)
    );

    return {
      verified: !!match,
      priceRange: match?.item?.offers ? 
        `${match.item.offers.lowPrice}-${match.item.offers.highPrice} ${match.item.offers.priceCurrency}` : 
        undefined,
      productImage: match?.item?.image
    };
  } finally {
    await browser.close();
  }
};
