import { createContext, useContext, useState } from 'react';

type PlaywrightStatus = {
  activeVerifications: number;
  activeSubmissions: number;
  lastError?: string;
};

const PlaywrightContext = createContext<{
  status: PlaywrightStatus;
  setStatus: React.Dispatch<React.SetStateAction<PlaywrightStatus>>;
} | null>(null);

export const PlaywrightProvider = ({ children }: { children: React.ReactNode }) => {
  const [status, setStatus] = useState<PlaywrightStatus>({
    activeVerifications: 0,
    activeSubmissions: 0
  });

  return (
    <PlaywrightContext.Provider value={{ status, setStatus }}>
      {children}
    </PlaywrightContext.Provider>
  );
};

export const usePlaywrightStatus = () => {
  const context = useContext(PlaywrightContext);
  if (!context) {
    throw new Error('usePlaywrightStatus must be used within a PlaywrightProvider');
  }
  return context;
};
